from typing import Dict, List, Set



def solve(string: str) -> int:
	pairs: Dict[int, int] = {}
	opens: List[int] = []
	for i, char in enumerate(string):
		if char == '(':
			opens.append(i)
		else:
			if opens:
				pairs[opens.pop()] = i

	if not pairs:
		return 0

	indexes_set: Set[int] = set()
	for start, end in pairs.items():
		indexes_set |= set(range(start, end + 1))
	indexes = sorted(list(indexes_set))

	longest = 0
	i = indexes[0]
	j = indexes[0]
	last = indexes[-1]
	while i < last:
		while j in indexes_set:
			j += 1

		if (length := j - i) > longest:
			longest = length

		j += 1
		i = j

	return longest



def test_solve():
	assert solve('(()') == 2
	assert solve(')()())') == 4
	assert solve('') == 0
	assert solve(')()())()()(') == 4
